import React, { useEffect } from 'react'
import { makeStyles } from '@material-ui/core/styles'
import Drawer from '@material-ui/core/Drawer'
import Box from '@material-ui/core/Box'
import AppBar from '@material-ui/core/AppBar'
import Toolbar from '@material-ui/core/Toolbar'
import Typography from '@material-ui/core/Typography'
import Divider from '@material-ui/core/Divider'
import Container from '@material-ui/core/Container'
import Link from '@material-ui/core/Link'
import { Grid, IconButton, CssBaseline, Button, Avatar } from '@material-ui/core'
import ListRouter from './ListRouter'
import { routes } from './routes'
import { ApmRoute } from '@elastic/apm-rum-react'
import logo from './logo2.png'
import clsx from 'clsx'
import MenuIcon from '@material-ui/icons/Menu'
import ChevronLeftIcon from '@material-ui/icons/ChevronLeft'

import {
  Switch,
  Redirect,
  Route
} from 'react-router-dom'
import { useSelector, useDispatch } from 'react-redux'
import { loadApp } from '../actions'

import DarkModeToggle from './utils/DarkModeToggle'
import PageTitle from './utils/PageTitle'
import UserDetails from './utils/UserDetails'

const drawerWidth = 240

const useStyles = makeStyles(theme => ({
  root: {
    display: 'flex'
  },
  appBar: {
    zIndex: theme.zIndex.drawer + 1
  },
  drawer: {
    width: drawerWidth,
    flexShrink: 0
  },
  drawerPaper: {
    width: drawerWidth
  },
  content: {
    flexGrow: 1
  },
  body: {
    display: 'flex',
    flexDirection: 'column',
    minHeight: '90vh'
  },
  copyright: {
    marginTop: 'auto'
  },
  title: {
    flexGrow: 1
  },  small: {
    width: theme.spacing(3),
    height: theme.spacing(3),
  },
  large: {
    width: theme.spacing(7),
    height: theme.spacing(7),
  },

  // necessary for content to be below app bar
  toolbar: theme.mixins.toolbar
}))

function Copyright () {
  return (
    <Typography variant='body2' color='textSecondary' align='center'>
      {'Copyright © '}
      <Link color='inherit' href='https://www.docudio.com/'>
        Docudio
      </Link>{' '}
      {new Date().getFullYear()}
      {'.'}
    </Typography>
  )
}

export default function Dashboard () {
  const classes = useStyles()
  const dispatch = useDispatch()
  const appLoaded = useSelector(state => state.common.appLoaded)

  useEffect(() => {
    if (!appLoaded) {
      dispatch(loadApp())
    }
  }, [dispatch, appLoaded])
  const [open, setOpen] = React.useState(true)

  const handleDrawerOpen = () => {
    setOpen(true)
  }

  const handleDrawerClose = () => {
    setOpen(false)
  }

  return (
    <div className={classes.root}>
      <CssBaseline />
      <AppBar position='fixed' className={classes.appBar}>
        <Toolbar>

          <Grid container direction='row'
            justify='flex-end' spacing={4} >
            <Grid item justify='flex-end' xl={2} sm={2} md={2} lg={2} >
              <IconButton
                color='inherit'
                aria-label='open drawer'
                onClick={handleDrawerOpen}
                edge='start'
                className={clsx(classes.menuButton, open && classes.hide)}
              >
                <MenuIcon />
              </IconButton>
            </Grid>

            <Grid item xl={6} sm={6} md={6} lg={6}>
              <Button size='large' className={classes.large} startIcon={<Avatar className={classes.large} src={logo} />} variant='contained' color='primary' > <Typography variant='h4'> Docudio </Typography> </Button>         {/*  <LegacysiteLink /><LoginButton /> */}
            </Grid>
            <Grid item justify='flex-end' xl={4} sm={4} md={4} lg={4}>
              <DarkModeToggle />
            </Grid>
          </Grid>

        </Toolbar>
      </AppBar>
      <Drawer
        variant='persistent'
        open={open}
        classes={{
          paper: classes.drawerPaper
        }}
        className={classes.drawer}
      >
        <div className={classes.toolbar} />
        <div className={classes.drawerHeader}>
          <IconButton onClick={handleDrawerClose}>
            <ChevronLeftIcon />
          </IconButton>
        </div>

        <Divider />
        <ListRouter />
        <UserDetails />
      </Drawer>
      <main className={classes.content}>
        <div className={classes.toolbar} />
        <Container className={classes.body} maxWidth={false}>
          <PageTitle />
          <Switch>
            {routes.map((route, index) => (
              route.private
                ? (
                  <PrivateRoute
                    strict={route.strict}
                    key={index}
                    path={route.path}
                    exact={route.exact}
                  >{route.main()}
                  </PrivateRoute>)
                : (
                  <ApmRoute
                    key={index}
                    path={route.path}
                    exact={route.exact}
                    component={() => (
                      route.main()
                    )}
                  />)
            ))}
          </Switch>
          <footer className={classes.copyright}>
            <Box pt={4}>
              <Copyright />
            </Box>
          </footer>
        </Container>
      </main>
    </div>
  )
}

function PrivateRoute ({ children, ...rest }) {
  const isAuthorized = useSelector(state => state.auth.isAuthorized)
  return (
    <Route
      {...rest} render={({ location }) => isAuthorized
        ? (children)
        : (<Redirect to={{ pathname: '/auth', state: { from: location } }} />)}
    />
  )
}
