import React from 'react'
import ReactDOM from 'react-dom'
import { Provider } from 'react-redux'
import '../index.css'
import store from '../store/index'
import { BrowserRouter } from 'react-router-dom'
import apm from '../rum'
import '../App.css'
import {MakeStore, createWrapper, Context} from 'next-redux-wrapper';
import Dashboard from './index'
import { ThemeProvider, createMuiTheme } from '@material-ui/core/styles'
import { Button } from '@material-ui/core'
import { useSelector } from 'react-redux'
import withRedux from "next-redux-wrapper";
import { SnackbarProvider } from 'notistack'
import Notifier from '../utils/Notifier'
import { withRouter } from 'next/router'
import App from 'next/app';
import { wrapper } from "../store";
import {AppInitialProps, AppContext} from 'next/app';
import {END} from 'redux-saga';

apm.setInitialPageLoadName('Self Service Landing Page')

function WrappedApp (props){
     const getInitialProps = async ({Component, ctx}: AppContext) => {
        // 1. Wait for all page actions to dispatch
        const pageProps = {
            ...(Component.getInitialProps ? await Component.getInitialProps(ctx) : {}),
        };

        // 2. Stop the saga if on server


        // 3. Return props
        return {
            pageProps,
        };
    };
      const ThemePreference = useSelector(state => state.theme.preferred)

  const theme = React.useMemo(
    () =>
      createMuiTheme({
        palette: {
          type: ThemePreference === true ? 'dark' : 'light',
          primary: { main: '#000000' },
          secondary: { main: '#dbdbdb' }
        },
        typography: { fontFamily: ['Montserrat'].join(',') }
      }),
    [ThemePreference]
  )
    theme.typography.subtitle1 = { 'font-family': ['Hammersmith One', 'sans-serif'] }
  theme.typography.subtitle2 = { 'font-family': ['Hammersmith One', 'sans-serif'] }

  const notistackRef = React.createRef()
  const onClickDismiss = key => () => {
    notistackRef.current.closeSnackbar(key)
  }

        const {Component, pageProps} =props;
        return     (    <ThemeProvider theme={theme}>

      <SnackbarProvider ref={notistackRef} action={(key) => (
        <Button onClick={onClickDismiss(key)}>
            Dismiss
        </Button>
      )}
      maxSnack={3} >

<Component {...pageProps} />      </SnackbarProvider >

    </ThemeProvider>)



}

export default wrapper.withRedux(WrappedApp);

apm.setInitialPageLoadName('Self Service Landing Page')
//makeStore function that returns a new store for every request
