import React, { useEffect } from 'react'
import { makeStyles } from '@material-ui/core/styles'
import Drawer from '@material-ui/core/Drawer'
import Box from '@material-ui/core/Box'
import AppBar from '@material-ui/core/AppBar'
import Toolbar from '@material-ui/core/Toolbar'
import Typography from '@material-ui/core/Typography'
import Divider from '@material-ui/core/Divider'

import Link from '@material-ui/core/Link'
import { Grid, IconButton, CssBaseline, Button, Avatar } from '@material-ui/core'
import clsx from 'clsx'
import MenuIcon from '@material-ui/icons/Menu'
import ChevronLeftIcon from '@material-ui/icons/ChevronLeft'
import { useRouter } from 'next/router'
import { useDispatch } from 'react-redux'
import PageLinks from '../utils/PageLinks'
import { loadApp } from '../actions'
import Home from '../sharedComponents/Home'
import DarkModeToggle from '../utils/DarkModeToggle'

const drawerWidth = 240

const useStyles = makeStyles(theme => ({
  root: {
    display: 'flex'
  },
  appBar: {
    zIndex: theme.zIndex.drawer + 1
  },
  drawer: {
    width: drawerWidth,
    flexShrink: 0
  },
  drawerPaper: {
    width: drawerWidth
  },
  content: {
    flexGrow: 1
  },
  body: {
    display: 'flex',
    flexDirection: 'column',
    minHeight: '90vh'
  },
  copyright: {
    marginTop: 'auto'
  },
  title: {
    flexGrow: 1
  },
  small: {
    width: theme.spacing(3),
    height: theme.spacing(3)
  },
  large: {
    width: theme.spacing(7),
    height: theme.spacing(7)
  },

  // necessary for content to be below app bar
  toolbar: theme.mixins.toolbar
}))

function Copyright () {
  return (
    <Typography variant='body2' color='textSecondary' align='center'>
      {'Copyright © '}
      <Link color='inherit' href='https://www.docudio.com/'>
        Docudio
      </Link>{' '}
      {new Date().getFullYear()}
      {'.'}
    </Typography>
  )
}

export default function Dashboard () {
  const classes = useStyles()
  const dispatch = useDispatch()
  const appLoaded = true
  const router = useRouter()
  useEffect(() => {
    if (!appLoaded) {
      dispatch(loadApp())
    }
  }, [dispatch, appLoaded])
  const [open, setOpen] = React.useState(true)

  const handleDrawerOpen = () => {
    setOpen(true)
  }

  const handleDrawerClose = () => {
    setOpen(false)
  }
  return (
    <div className={classes.root}>
      <CssBaseline />
      <AppBar position='fixed' className={classes.appBar}>
        <Toolbar>

          <Grid container direction='row'
            spacing={4} >
            <Grid item xl={2} sm={2} md={2} lg={2} >
              <IconButton
                color='inherit'
                aria-label='open drawer'
                onClick={handleDrawerOpen}
                edge='start'
                className={clsx(classes.menuButton, open && classes.hide)}
              >
                <MenuIcon />
              </IconButton>
            </Grid>

            <Grid item xl={6} sm={6} md={6} lg={6}>
              <Button size='large' className={classes.large} startIcon={<Avatar className={classes.large} src='/logo2.png' />} variant='contained' color='primary' > <Typography variant='h4'> Docudio </Typography> </Button>         {/*  <LegacysiteLink /><LoginButton /> */}
            </Grid>
            <Grid item xl={4} sm={4} md={4} lg={4}>
              <DarkModeToggle />
            </Grid>
          </Grid>

        </Toolbar>
      </AppBar>
      <Drawer
        variant='persistent'
        open={open}
        classes={{
          paper: classes.drawerPaper
        }}
        className={classes.drawer}
      >
        <div className={classes.toolbar} />
        <div className={classes.drawerHeader}>
          <IconButton onClick={handleDrawerClose}>
            <ChevronLeftIcon />
          </IconButton>
        </div>
<PageLinks />
        <Divider />
      </Drawer>
      <main className={classes.content}>
        <div className={classes.toolbar} />
        <footer className={classes.copyright}>
          <Box pt={4}>
          <Home />

            <Copyright />
          </Box>
        </footer>
      </main>
    </div>
  )
}
